import { useState, useRef } from 'react';
import Header from './components/Header.js';
import Notes from './components/Notes.js';
import Footer from './components/Footer.js';
import Table from './components/Table.js';
import MainDetails from './components/MainDetails.js';
import ClientDetails from './components/ClientDetails.js';
import Dates from './components/Dates.js';
import TableForm from './components/TableForm.js';
import ReactToPrint from 'react-to-print';

function App() {
  const [showInvoice, setShowInvoice] = useState(false)
  const [name, setName] = useState("Rohan Agarwal")
  const [address, setAddress] = useState("Agarwal Nursing Home, Amroha")
  const [phone, setPhone] = useState("123456123")
  const [invoiceNumber, setInvoiceNumber] = useState("1002")
  const [admissionDate, setAdmissionDate] = useState("2022/07/02")
  const [dischargeDate, setDischargeDate] = useState("2/08/2022")
  const [notes, setNotes] = useState("No additional Notes for now.")
  const [description, setDescription] = useState("")
  const [quantity, setQuantity] = useState("")
  const [price, setPrice] = useState("")
  const [amount, setAmount] = useState("")
  const [list, setList] = useState([])
  const [total, setTotal] = useState(0)

  const componentRef = useRef()

  const handlePrint = () => {
    window.print()
  }

  return (
    <>
      <main className='m-5 p-5 md:max-w-xl md:mx-auto lg:max-w-2xl xl:max-w-4xl bg-white ronded shadow'>
      {showInvoice ?  (
        <>
          <ReactToPrint 
            trigger={() => <button
                            className='ml-5 bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 hover:bg-transparent hover:text-blue-500 transition-all duration-300'
                          >
                              Print/Download
                            </button>} 
            content ={() => componentRef.current}
          />
          <div ref={componentRef} className='p-5'>

            <Header handlePrint = {handlePrint} />

            {/* <MainDetails name={name} address={address}/> */}

            <Dates invoiceNumber={invoiceNumber} invoiceDate={invoiceDate} dueDate={dueDate} />

            <ClientDetails clientName={clientName} clientAddress={clientAddress} />

            <Table list={list} total = {total} />

            <Notes notes={notes}/>

            <Footer 
              name={name} 
              website={website} 
              email={email}
              phone={phone}
              bankAccount={bankAccount}
              bankName={bankName}
            />



            </div> 

            <button 
              onClick={() => setShowInvoice(false)}
              className='mt-5 bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 hover:bg-transparent hover:text-blue-500 transition-all duration-300'> 
              Edit Information
            </button>

            </>
          ): (
            <>
            {/* name, address, email, phone, bank name, bank account number, client name, client address, invoice number, invoice date, due date, notes */}
            <div className='flex flex-col justify-center'>

              <article className='md:grid grid-cols-2 gap-10'>
                <div className='flex flex-col'>
                  <label htmlFor='name'>Full Name</label>
                  <input 
                    type ='text' 
                    name='text' 
                    id='text' 
                    placeholder='Full Name' 
                    autoComplete='off'
                    value = {name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </div>

                <div className='flex flex-col'>
                  <label htmlFor='phone'>Enter your phone</label>
                  <input 
                    type ='text' 
                    name='phone' 
                    id='phone' 
                    placeholder='Enter your phone' 
                    autoComplete='off'
                    value = {phone}
                    onChange={(e) => {
                      return setPhone(e.target.value);
                    }}
                  />
                </div>
              </article>

              <div className='flex flex-col'>
                  <label htmlFor='address'>Enter your address</label>
                  <input 
                    type ='text' 
                    name='text' 
                    id='address' 
                    placeholder='Enter your address' 
                    autoComplete='off'
                    value = {address}
                    onChange={(e) => setAddress(e.target.value)}
                  />
                </div>

              {/* <article className='md:grid grid-cols-2 gap-10'>
                <div className='flex flex-col'>
                  <label htmlFor='bankName'>Enter your bank name</label>
                  <input 
                    type ='text' 
                    name='bankName' 
                    id='bankName' 
                    placeholder='Enter your bank name' 
                    autoComplete='off'
                    value = {bankName}
                    onChange={(e) => {
                      return setBankName(e.target.value);
                    }}
                  />
                </div>

                <div className='flex flex-col'>
                  <label htmlFor='bankAccount'>Enter your bank account number</label>
                  <input 
                    type ='text' 
                    name='bankAccount' 
                    id='bankAccount' 
                    placeholder='Enter your bank account number' 
                    autoComplete='off'
                    value = {bankAccount}
                    onChange={(e) => {
                      return setBankAccount(e.target.value);
                    }}
                  />
                </div>
              </article> */}

              {/* <article className='md:grid grid-cols-2 gap-10 md:mt-16'>
                <div className='flex flex-col'>
                  <label htmlFor='clientName'>Enter your client's name</label>
                  <input 
                    type ='text' 
                    name='clientName' 
                    id='clientName' 
                    placeholder="Enter your client's name" 
                    autoComplete='off'
                    value = {clientName}
                    onChange={(e) => {
                      return setClientName(e.target.value);
                    }}
                  />
                </div>

                <div className='flex flex-col'>
                  <label htmlFor='clientAddress'>Enter your client's address</label>
                  <input 
                    type ='text' 
                    name='clientAddress' 
                    id='clientAddress' 
                    placeholder="Enter your client's address" 
                    autoComplete='off'
                    value = {clientAddress}
                    onChange={(e) => {
                      return setClientAddress(e.target.value);
                    }}
                  />
                </div>
              </article> */}

              <article className='md:grid grid-cols-2 gap-10'>

                <div className='flex flex-col'>
                  <label htmlFor='invoiceDate'>Enter date of admission</label>
                  <input 
                    type ='date' 
                    name='admissionDate' 
                    id='admissionDate' 
                    placeholder="Enter your admission date" 
                    autoComplete='off'
                    value = {admissionDate}
                    onChange={(e) => {
                      return setAdmissionDate(e.target.value);
                    }}
                  />
                </div>

                <div className='flex flex-col'>
                  <label htmlFor='dueDate'>Enter your due date</label>
                  <input 
                    type ='date' 
                    name='dueDate' 
                    id='dueDate' 
                    placeholder="Enter your due date" 
                    autoComplete='off'
                    value = {dueDate}
                    onChange={(e) => {
                      return setDueDate(e.target.value);
                    }}
                  />
                </div>
              </article>

              {/* Table Form*/}
              <article>
                <TableForm 
                  description={description} setDescription={setDescription}
                  quantity={quantity} setQuantity={setQuantity}
                  price={price} setPrice={setPrice}
                  amount={amount} setAmount={setAmount}
                  list={list} setList={setList}
                  total = {total} setTotal={setTotal}
                />
              </article>

              <label htmlFor='notes'>Additional Notes</label>
              <textarea 
                name = 'notes'
                id='notes'
                cols='30'
                rows='10'
                placeholder='Additonal Notes to the client'
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
              ></textarea>

              <button onClick = { 
                () => setShowInvoice(true)
                } 
                className='bg-blue-500 text-white 
                font-bold py-2 px-8 rounded shadow border-2 
                border-blue-500 hover:bg-transparent hover:text-blue-500 
                transition-all duration-300'>
                Preview Invoice
              </button>

            </div>
            </>

      )}

      </main>
    </>
  );
}

export default App;
