import { useState } from 'react';
import Header from './components/Header.js';
import Notes from './components/Notes.js';
import Footer from './components/Footer.js';
import Table from './components/Table.js';
import MainDetails from './components/MainDetails.js';
import ClientDetails from './components/ClientDetails.js';
import Dates from './components/Dates.js';

function App() {
  const [showInvoice, setShowInvoice] = useState(false)


  const handlePrint = () => {
    window.print()
  }

  return (
    <>
      <main className='m-5 p-5 xl:max-w-4xl xl:mx-auto bg-white ronded shadow'>

       {showInvoice ?  (<div>

            <Header handlePrint = {handlePrint} />

            <MainDetails />

            <ClientDetails />

            <Dates />

            <Table />

            <Notes />

            <Footer />

            <button 
            onClick={() => setShowInvoice(false)}
            className='bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 hover:bg-transparent hover:text-blue-500 transition-all duration-300'> 
            Edit Information</button>

            </div> 
          ): (
            <>
            <div className='flex flex-col justify-center'>
              <input type ='text' name='text' id='text' placeholder='Enter your name' autoComplete='off' />

              <button onClick = { 
                () => setShowInvoice(true)
              } className='bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 hover:bg-transparent hover:text-blue-500 transition-all duration-300'>
                Preview Invoice
                </button>

            </div>
            </>

      )}

      </main>
    </>
  );
}

export default App;
