import { useState } from 'react';
import Header from './components/Header.js';
import Notes from './components/Notes.js';
import Footer from './components/Footer.js';
import Table from './components/Table.js';
import MainDetails from './components/MainDetails.js';
import ClientDetails from './components/ClientDetails.js';
import Dates from './components/Dates.js';

function App() {
  const [showInvoice, setShowInvoice] = useState(false)
  const [name, setName] = useState("")
  const [address, setAddress] = useState("")
  const [email, setEmail] = useState("")
  const [phone, setPhone] = useState("")
  const [bankName, setBankName] = useState("")
  const [bankAccount, setBankAccount] = useState("")
  const [website, setWebsite] = useState("")
  const [clientName, setClientName] = useState("")
  const [clientAddress, setClientAddress] = useState("")
  const [invoiceNumber, setInvoiceNumber] = useState("")
  const [invoiceDate, setInvoiceDate] = useState("")
  const [dueDate, setDueDate] = useState("")
  const [notes, setNotes] = useState("")



  const handlePrint = () => {
    window.print()
  }

  return (
    <>
      <main className='m-5 p-5 xl:max-w-4xl xl:mx-auto bg-white ronded shadow'>

       {showInvoice ?  (<div>

            <Header handlePrint = {handlePrint} />

            <MainDetails />

            <ClientDetails />

            <Dates />

            <Table />

            <Notes />

            <Footer />

            <button 
            onClick={() => setShowInvoice(false)}
            className='mt-5 bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 hover:bg-transparent hover:text-blue-500 transition-all duration-300'> 
            Edit Information</button>

            </div> 
          ): (
            <>
            {/* name, address, email, phone, bank name, bank account number, client name, client address, invoice number, invoice date, due date, notes */}
            <div className='flex flex-col justify-center'>
              <label htmlFor='name'>Enter your name</label>
              <input 
                type ='text' 
                name='text' 
                id='text' 
                placeholder='Enter your name' 
                autoComplete='off'
                value = {name}
                onChange={(e) => setName(e.target.value)}
              />

              <label htmlFor='address'>Enter your name</label>
                <input 
                  type ='text' 
                  name='text' 
                  id='address' 
                  placeholder='Enter your address' 
                  autoComplete='off'
                  value = {address}
                  onChange={(e) => setAddress(e.target.value)}
                />

                <label htmlFor='email'>Enter your name</label>
                  <input 
                    type ='email' 
                    name='email' 
                    id='email' 
                    placeholder='Enter your email' 
                    autoComplete='off'
                    value = {email}
                    onChange={() => setEmail(e.target.value)}
                  />

                <label htmlFor='address'>Enter your name</label>
                  <input 
                    type ='text' 
                    name='text' 
                    id='text' 
                    placeholder='Enter your address' 
                    autoComplete='off'
                    value = {address}
                    onChange={() => setAddress(e.target.value)}
                  />

              <button onClick = { 
                () => setShowInvoice(true)
              } className='bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 hover:bg-transparent hover:text-blue-500 transition-all duration-300'>
                Preview Invoice
                </button>

            </div>
            </>

      )}

      </main>
    </>
  );
}

export default App;
